parameters.jsonBody:
={
  "model": "sonar",
  "messages": [
    {
      "role": "system",
      "content": "You are a Formula 1 news researcher. Extract the top trending F1 stories from the last 24 hours. Return results ONLY as a JSON array with no markdown formatting."
    },
    {
      "role": "user",
      "content": "Find the top 5 trending Formula 1 news stories RIGHT NOW. For each story, return a JSON object with these EXACT fields: headline (string), trending_score (number 1-10), key_points (string, 2-3 sentences), teams_drivers (string, teams/drivers mentioned), content_ideas (string), sources (string, comma-separated URLs). Return ONLY the JSON array, no other text or markdown."
    }
  ],
  "temperature": 0.2,
  "max_tokens": 1500
}

parameters.jsCode:
const response = $input.item.json;
const currentDate = new Date().toISOString().split('T')[0];
const currentTime = new Date().toLocaleTimeString('en-US', { 
  hour12: false, 
  hour: '2-digit', 
  minute: '2-digit',
  timeZone: 'Asia/Singapore'
});

let content = response.choices[0].message.content;
let topics = [];

try {
  content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  topics = JSON.parse(content);
  if (!Array.isArray(topics)) {
    topics = [topics];
  }
} catch (e) {
  if (content.includes('##')) {
    const sections = content.split('##').slice(1);
    topics = sections.slice(0, 5).map((section) => {
      const lines = section.split('\n').filter(l => l.trim());
      const headline = lines[0].replace(/^#+\s*/, '').trim();
      const bodyText = lines.slice(1).join(' ');
      return {
        headline: headline.substring(0, 200),
        trending_score: 8,
        key_points: bodyText.substring(0, 300).trim(),
        teams_drivers: extractTeams(headline + ' ' + bodyText),
        content_ideas: 'Reaction video',
        sources: extractUrls(section).slice(0, 3).join(', ')
      };
    });
  } else {
    topics = [{
      headline: 'F1 Research Summary',
      trending_score: 7,
      key_points: content.substring(0, 300),
      teams_drivers: 'General',
      content_ideas: 'General content',
      sources: ''
    }];
  }
}

function extractTeams(text) {
  const teams = ['Mercedes', 'Red Bull', 'Ferrari', 'McLaren', 'Aston Martin', 'Alpine', 'Williams', 'RB', 'Haas', 'Sauber', 'Verstappen', 'Hamilton', 'Leclerc', 'Norris', 'Russell'];
  const found = teams.filter(team => text.toLowerCase().includes(team.toLowerCase()));
  return found.length > 0 ? found.slice(0, 3).join(', ') : 'General';
}

function extractUrls(text) {
  const urlRegex = /https?:\/\/[^\s\]\)\"]+/g;
  return text.match(urlRegex) || [];
}

if (!topics || topics.length === 0) {
  topics = [{ headline: 'No topics', trending_score: 5, key_points: 'None', teams_drivers: 'General', content_ideas: 'General', sources: '' }];
}

const sheetRows = topics.slice(0, 5).map(topic => ({
  json: {
    Date: currentDate,
    ResearchTime: currentTime,
    Status: (topic.trending_score >= 8) ? 'HIGH_PRIORITY' : 'COMPLETED',
    TopicEvent: (topic.headline || 'Untitled').substring(0, 300),
    TrendingScore: topic.trending_score || 5,
    SourceURLs: (topic.sources || '').substring(0, 500),
    KeyPoints: (topic.key_points || 'No details').substring(0, 500),
    ContentIdeas: (topic.content_ideas || 'Reaction video').substring(0, 200),
    AssignedWorkflow: (topic.trending_score >= 8) ? 'Long-form + Short' : 'Single Image',
    Priority: (topic.trending_score >= 8) ? 'HIGH' : 'MEDIUM',
    TeamDriver: (topic.teams_drivers || 'General').substring(0, 200),
    Notes: '',
    PerplexityQuery: 'Latest F1 news ' + currentDate
  }
}));

return sheetRows;

